import React, { useState } from 'react';
import './style/login.css';
import logo from './images/logo-cs.png';


export default () => {

    const [userName, setUserName] = useState(null)
    const [password, setPassword] = useState(null)

    function enviarFormulario() {
        fetch('http://localhost:8081/verificar', {
            method: 'POST',
            body: JSON.stringify({username : userName, password : password})
        }).then((res) => {
            console.log("O usuario entrou com sucesso e a promisse e " + res)
        }).catch((err) => {
            console.log(err)
        })
    }

    return(
        <div className="mybody">

                <div className="login">
                    <div className="arealogo">
                        <div></div>
                        <img src={logo} height="43px" width="90px" className="logocs"/>
                        <div></div>
                    </div>

                    <div className="login-box">
                        <h1>Login</h1>
                        <form method="POST" action="/verificar">
                            <div class="textbox">
                                <i class="fas fa-user"></i>
                                <input type="text" placeholder="Username" name="username" onChange={e => setUserName(e.target.value)}/>
                            </div>

                            <div class="textbox">
                                <i class="fas fa-lock"></i>
                                <input type="password" placeholder="Password" name="password" onChange={e => setPassword(e.target.value)}/>
                            </div>

                            <input type="button" class="btn" value="Entrar" onClick={enviarFormulario}/>
                            <input type="button" class="btn_roxo" value="Cadastrar"/>
                        </form>

                    </div>
                </div>
        </div>

    )
}
