import React, { useState, useEffect } from 'react';
import './style/login.css';
import logo from './images/logo-cs.png';
import axios from 'axios'


export default () => {

    const [userName, setUserName] = useState('deni18')
    const [password, setPassword] = useState('123123')
    const [token, setToken] = useState()

      useEffect(()=>{
            axios.post('/verificar', { username : userName, password: password}).then(result => {
                console.log(result.data)
                setToken(result.date)
                console.log(token)

            })
        },[])

    function enviarFormulario() {
        fetch('http://localhost:8081/verificar', {
            method: 'POST',
            body: JSON.stringify({username : userName, password : password})
        }).then((res) => {
            console.log("O usuario entrou com sucesso e a promisse e " + res)
        }).catch((err) => {
            console.log(err)
        })
    }

    function teste() {
        fetch('http://localhost:8081/', {
            method: 'GET'
        }).then((res) => { console.log("Deu certo")})

    }

    return(
        <div className="mybody">

                <div className="login">
                    <div className="arealogo">
                        <div></div>
                        <img src={logo} height="43px" width="90px" className="logocs"/>
                        <div></div>
                    </div>

                    <div className="login-box">
                        <h1>Login</h1>
                        <form method="POST" action="/verificar">
                            <div class="textbox">
                                <i class="fas fa-user"></i>
                                <input type="text" placeholder="Username" name="username" onChange={e => setUserName(e.target.value)}/>
                            </div>

                            <div class="textbox">
                                <i class="fas fa-lock"></i>
                                <input type="password" placeholder="Password" name="password" onChange={e => setPassword(e.target.value)}/>
                            </div>

                            <input type="button" class="btn" value="Entrar" onClick={teste}/>
                            <input type="button" class="btn_roxo" value="Cadastrar"/>
                        </form>

                    </div>
                </div>
        </div>

    )
}
